CREATE OR REPLACE FUNCTION obter_versao_tasy_data (dt_referencia_p timestamp, ie_tipo_p text, nr_seq_intencao_uso_p bigint default 2) RETURNS varchar AS $body$
DECLARE


/*
IE_TIPO_P
'D' - Diaria
'S' - Semanal
*/
cd_versao_w  varchar(15)  := null;
dt_versao_w  timestamp;


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
  if (ie_tipo_p = 'D') then
    select  min(a.dt_versao)
    into STRICT  dt_versao_w
    from  aplicacao_tasy_versao a
    where  a.cd_aplicacao_tasy  in ('Tasy','HIS')
    and  a.dt_versao  > dt_referencia_p
    and  a.dt_versao  < trunc(a.dt_versao,'dd') + 1
    and  coalesce(a.nr_seq_intencao_uso, 2) = nr_seq_intencao_uso_p;
  elsif (ie_tipo_p = 'S') then
    select  min(a.dt_versao)
    into STRICT  dt_versao_w
    from  aplicacao_tasy_versao a
    where  a.cd_aplicacao_tasy  in ('Tasy','HIS')
    and  a.dt_versao  > dt_referencia_p
    and  a.dt_versao  < trunc(a.dt_versao,'dd') + 7
    and  coalesce(a.nr_seq_intencao_uso, 2) = nr_seq_intencao_uso_p;
  elsif (ie_tipo_p  = 'V') then
    select  max(a.dt_versao)
    into STRICT  dt_versao_w
    from  aplicacao_tasy_versao a
    where  a.cd_aplicacao_tasy  in ('Tasy','HIS')
    and  a.dt_versao  < dt_referencia_p
    and  coalesce(a.nr_seq_intencao_uso, 2) = nr_seq_intencao_uso_p;
  end if;

  if (dt_versao_w IS NOT NULL AND dt_versao_w::text <> '') then
    select  max(a.cd_versao)
    into STRICT  cd_versao_w
    from  aplicacao_tasy_versao a
    where  a.cd_aplicacao_tasy  in ('Tasy','HIS')
    and  a.dt_versao  = dt_versao_w
    and  coalesce(a.nr_seq_intencao_uso, 2) = nr_seq_intencao_uso_p;
  end if;
end if;

return  cd_versao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
