CREATE OR REPLACE FUNCTION obter_vias_adm_forma ( nr_seq_forma_p bigint) RETURNS varchar AS $body$
DECLARE


ds_via_w		varchar(80);
ds_retorno_w		varchar(4000);


C01 CURSOR FOR
SELECT	obter_desc_expressao(b.cd_exp_via, b.ds_via)
from	anvisa_forma_farm_via a,
	anvisa_via_administracao b
where	b.nr_sequencia 		= a.nr_seq_via_adm
and	a.nr_seq_forma_farm	= nr_seq_forma_p;



BEGIN


open C01;
loop
	fetch C01 into
		ds_via_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := ds_via_w || ', ' || ds_retorno_w;
		end;
end loop;
close C01;


ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w) - 2) || '.';


return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
