CREATE OR REPLACE FUNCTION obter_via_anvisa (ie_via_administracao_p text) RETURNS varchar AS $body$
DECLARE


ie_abreviacao_via_w	varchar(15);


BEGIN

select	max(ie_abreviacao_via)
into STRICT	ie_abreviacao_via_w
from 	anvisa_via_administracao a,
	via_aplicacao b
where  	b.nr_seq_via_anvisa = a.nr_sequencia
and	upper(b.ie_via_aplicacao)  = upper(ie_via_administracao_p);


return	coalesce(ie_abreviacao_via_w,ie_via_administracao_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
