CREATE OR REPLACE FUNCTION obter_via_aplic_analise_disp ( cd_material_p text, nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE

 
			 
cd_setor_atendimento_w	integer;
cd_pessoa_fisica_w		varchar(10);
qt_idade_w  			varchar(50);
qt_peso_w				real;
ie_via_aplicacao_w		varchar(5);
nr_atendimento_w		bigint;
			

BEGIN 
 
select	max(cd_pessoa_fisica), 
		max(cd_setor_atendimento), 
		max(qt_peso), 
		max(nr_atendimento) 
into STRICT	cd_pessoa_fisica_w, 
		cd_setor_atendimento_w, 
		qt_peso_w, 
		nr_atendimento_w 
from	prescr_medica 
where 	nr_prescricao	= nr_prescricao_p;
 
qt_idade_w 		:= obter_idade_pf(cd_pessoa_fisica_w, clock_timestamp(), 'A');
ie_via_aplicacao_w 	:= obter_padrao_param_prescr(nr_atendimento_w, cd_material_p, null, cd_setor_atendimento_w, cd_pessoa_fisica_w, qt_idade_w, qt_peso_w, 'N', 'V', null);
     
if (coalesce(ie_via_aplicacao_w::text, '') = '') then 
	select	ie_via_aplicacao 
	into STRICT	ie_via_aplicacao_w 
	from 	material  
	where 	cd_material = cd_material_p;
end if;
 
return	ie_via_aplicacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
