CREATE OR REPLACE FUNCTION obter_via_usuario (ie_via_anvisa_p text) RETURNS varchar AS $body$
DECLARE


ie_abreviacao_via_w	varchar(15);


BEGIN

select	max(b.ie_via_aplicacao)
into STRICT	ie_abreviacao_via_w
from 	anvisa_via_administracao a,
	via_aplicacao b
where  	b.nr_seq_via_anvisa = a.nr_sequencia
and	upper(a.ie_abreviacao_via)  = upper(ie_via_anvisa_p)
and	coalesce(b.ie_situacao, 'A')	= 'A';


return	coalesce(ie_abreviacao_via_w,ie_via_anvisa_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
