CREATE OR REPLACE FUNCTION obter_vigencia_conta_contabil ( cd_conta_contabil_p text, ie_resultado_p text) RETURNS varchar AS $body$
DECLARE


/*ie_resultado_p
I	= Vigencia inicio
F	= Vigencia fim
*/
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w		timestamp;
dt_resultado_w		timestamp;


BEGIN

begin
select	dt_inicio_vigencia,
	dt_fim_vigencia
into STRICT	dt_inicio_vigencia_w,
	dt_fim_vigencia_w
from	conta_contabil
where	cd_conta_contabil = cd_conta_contabil_p;
exception when others then
	dt_inicio_vigencia_w	:= null;
	dt_fim_vigencia_w		:= null;
end;

if (ie_resultado_p = 'I') then
	dt_resultado_w	:= dt_inicio_vigencia_w;
elsif (ie_resultado_p = 'F') then
	dt_resultado_w	:= dt_fim_vigencia_w;
end if;

return TO_CHAR(dt_resultado_w,'DD/MM/YYYY');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
