CREATE OR REPLACE FUNCTION obter_vigencia_contrato ( nr_seq_contrato_p bigint, nr_seq_regra_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

/*ie_opcao_p
I - Dt inicio vigencia
F - Dt fim vigencia*/
dt_inicio_vigencia_w		timestamp;
dt_fim_vigencia_w			timestamp;
nr_seq_regra_w			bigint;


BEGIN

/*Se informar a sequencia da regra, busca as datas de vigencia daquela sequencia.*/

if (nr_seq_regra_p > 0) then
	select	dt_inicio_vigencia,
		dt_fim_vigencia
	into STRICT	dt_inicio_vigencia_w,
		dt_fim_vigencia_w
	from	contrato_regra_nf
	where	nr_sequencia = nr_seq_regra_p;
else
	/*senão busca da maior sequencia, nos casos que só tem 1 regra. Neste caso pode passar 0(zero) no nr_seq_regra_p*/

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from	contrato_regra_nf
	where	nr_seq_contrato = nr_seq_contrato_p;

	if (nr_seq_regra_w > 0) then
		select	dt_inicio_vigencia,
			dt_fim_vigencia
		into STRICT	dt_inicio_vigencia_w,
			dt_fim_vigencia_w
		from	contrato_regra_nf
		where	nr_sequencia = nr_seq_regra_w;
	end if;
end if;

if (ie_opcao_p = 'I') then
	return	dt_inicio_vigencia_w;
elsif (ie_opcao_p = 'F') then
	return	dt_fim_vigencia_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
