CREATE OR REPLACE FUNCTION obter_vinculo_medico ( cd_medico_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_empresa_w		smallint;
qt_medico_w		bigint;
ie_vinculo_medico_w	smallint;

C01 CURSOR FOR
	SELECT	ie_vinculo_medico
	from	medico_estabelecimento
	where	cd_pessoa_fisica = cd_medico_p
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	and	coalesce(cd_empresa, cd_empresa_w) = cd_empresa_w
	and	(ie_vinculo_medico IS NOT NULL AND ie_vinculo_medico::text <> '')
	order by coalesce(cd_estabelecimento,0),
		coalesce(cd_empresa,0);


BEGIN

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

select	count(*)
into STRICT	qt_medico_w
from	medico_estabelecimento
where	cd_pessoa_fisica = cd_medico_p
and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
and	coalesce(cd_empresa, cd_empresa_w) = cd_empresa_w
and	(ie_vinculo_medico IS NOT NULL AND ie_vinculo_medico::text <> '');

if (qt_medico_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_vinculo_medico_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_vinculo_medico_w:= ie_vinculo_medico_w;
		end;
	end loop;
	close C01;
else
	select	coalesce(max(ie_vinculo_medico),0)
	into STRICT	ie_vinculo_medico_w
	from	medico
	where	cd_pessoa_fisica = cd_medico_p;
end if;

return	ie_vinculo_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
