CREATE OR REPLACE FUNCTION obter_vinculo_parentesco ( CD_PESSOA_FISICA_P text, CD_PESSOA_FAMILIA_P text) RETURNS bigint AS $body$
DECLARE


DS_RETORNO_W		bigint;


BEGIN

	IF (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '' AND CD_PESSOA_FAMILIA_P IS NOT NULL AND CD_PESSOA_FAMILIA_P::text <> '') THEN


		SELECT coalesce(MAX(NR_SEQ_GRAU_PARENTESCO),'0')
		INTO STRICT DS_RETORNO_W
		FROM PF_FAMILIA
		WHERE CD_PESSOA_FISICA = CD_PESSOA_FISICA_P
		AND CD_PESSOA_FAMILIA = CD_PESSOA_FAMILIA_P;

	END IF;

	RETURN DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
