CREATE OR REPLACE FUNCTION obter_visao_pais_usage_config ( nr_seq_visao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_visao_ref_w	tabela_visao_pais.nr_seq_visao_ref%type;
nr_seq_localidade_w 	integer;

BEGIN

nr_seq_localidade_w := coalesce(Obter_Nr_Seq_Locale(wheb_usuario_pck.get_nm_usuario), 1);

if (nr_seq_localidade_w <> 1) then	
	begin
	select	max(nr_seq_visao_ref)
	into STRICT	nr_seq_visao_ref_w
	from	tabela_visao_pais
	where	nr_seq_visao = nr_seq_visao_p
	and 	cd_pais = nr_seq_localidade_w;
	
	if (coalesce(nr_seq_visao_ref_w,0) = 0) then
		begin
		select	max(nr_seq_visao_ref)
		into STRICT	nr_seq_visao_ref_w
		from	tabela_visao_pais
		where	nr_seq_visao = nr_seq_visao_p
		and 	cd_pais = 10;
		end;
	end if;
	end;
end if;

return coalesce(nr_seq_visao_ref_w, nr_seq_visao_p);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
