CREATE OR REPLACE FUNCTION obter_visibilidade_menu ( nr_seq_objeto_p bigint, ie_configuravel_p text, ie_visivel_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_visible_w	varchar(1);
qt_registro_w	integer;

C01 CURSOR FOR
	SELECT	ie_visible
	from	funcao_popup_regra
	where	nr_seq_objeto = nr_seq_objeto_p
	and		ie_tipo_regra = 'E'
	and		coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
	and		coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p
	and		coalesce(nm_usuario_regra, nm_usuario_p) = nm_usuario_p
	order by
	coalesce(nm_usuario_regra,'ZZZZZZZZZZZZZ'),
	coalesce(cd_perfil,0),
	coalesce(cd_estabelecimento,0) LIMIT 1;


BEGIN

if (ie_configuravel_p = 'N') then
	return ie_visivel_p;
end if;

open C01;
loop
fetch C01 into
	ie_visible_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (ie_configuravel_p = 'S') then
	return	coalesce(ie_visible_w, 'N');
end if;

return	coalesce(ie_visible_w, 'S');

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
