CREATE OR REPLACE FUNCTION obter_visualiza_registro_estab ( nr_atendimento_p bigint, vl_param_299_p text default null, vl_param_1410_p text default null) RETURNS varchar AS $body$
DECLARE


vl_return_w			varchar(1) := 'S';
vl_param_299_w		varchar(1);
vl_param_1410_w		varchar(255);
cd_estab_atend_w	atendimento_paciente.cd_estabelecimento%type;
cd_estab_regra_w	pep_regra_estab.cd_estabelecimento%type;
ds_lista_estab_regra_w	varchar(4000);

c01 CURSOR FOR
SELECT 	cd_estabelecimento
from 	pep_regra_estab;


BEGIN

if (coalesce(vl_param_299_p::text, '') = '') then
	vl_param_299_w := obter_param_usuario(281, 299, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, vl_param_299_w);
else
	vl_param_299_w := vl_param_299_p;
end if;

if((vl_param_299_w = 'N') or (coalesce(nr_atendimento_p,0) = 0)) then
	vl_return_w := 'S';
elsif (vl_param_299_w = 'S') then

	if (coalesce(vl_param_1410_p::text, '') = '') then
		vl_param_1410_w := obter_param_usuario(281, 1410, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, vl_param_1410_w);
	else
		vl_param_1410_w := vl_param_1410_p;
	end if;

	select 	obter_estab_atend(nr_atendimento_p)
	into STRICT 	cd_estab_atend_w
	;

	if (obter_se_contido_char(cd_estab_atend_w, coalesce(vl_param_1410_w,wheb_usuario_pck.get_cd_estabelecimento)) = 'S') then
		vl_return_w := 'S';
	else
		vl_return_w := 'N';
	end if;
elsif (vl_param_299_w = 'D') then

	open c01;
	loop
	fetch c01 into
		cd_estab_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (coalesce(ds_lista_estab_regra_w::text, '') = '') then
			ds_lista_estab_regra_w := cd_estab_regra_w;
		else
			ds_lista_estab_regra_w := ds_lista_estab_regra_w ||','||cd_estab_regra_w;
		end if;
	end loop;
	close c01;

	select 	obter_estab_atend(nr_atendimento_p)
	into STRICT 	cd_estab_atend_w
	;

	if (obter_se_contido_char(cd_estab_atend_w, coalesce(ds_lista_estab_regra_w,wheb_usuario_pck.get_cd_estabelecimento)) = 'S') then
		vl_return_w := 'S';
	else
		vl_return_w := 'N';
	end if;

end if;

return vl_return_w;
