CREATE OR REPLACE FUNCTION obter_vl_abaixar_bord_receb (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_saldo_titulo_w		double precision;
vl_desconto_w		double precision;
vl_nota_credito_w		double precision;
vl_abaixar_w		double precision;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	coalesce(max(vl_saldo_titulo),0),
		coalesce(max(vl_desc_previsto),0)
	into STRICT	vl_saldo_titulo_w,
		vl_desconto_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;

	select	coalesce(sum(a.vl_nota_credito),0)
	into STRICT	vl_nota_credito_w
	from	titulo_receber_nc a
	where	a.nr_titulo_rec = nr_titulo_p
	and	coalesce(a.nr_seq_liq::text, '') = '';

	vl_abaixar_w := coalesce(vl_saldo_titulo_w,0) - coalesce(vl_nota_credito_w,0) - coalesce(vl_desconto_w,0);
end if;

return coalesce(vl_abaixar_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
