CREATE OR REPLACE FUNCTION obter_vl_amort_pago_contrato (nr_seq_parcela_p bigint) RETURNS bigint AS $body$
DECLARE


vl_amort_w	double precision := 0;


BEGIN

select	coalesce(max(a.vl_amortizacao),0)
into STRICT	vl_amort_w
from	emprest_financ_parc a
where	a.nr_sequencia = nr_seq_parcela_p
and	coalesce(a.dt_cancelamento::text, '') = ''
and	exists (SELECT 1
		from titulo_pagar x
		where x.nr_titulo = a.nr_titulo
		and (x.dt_liquidacao IS NOT NULL AND x.dt_liquidacao::text <> ''));

return	vl_amort_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
