SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_apres_prot_web (nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


vl_total_apres_w	varchar(50);
vl_total_apres_proc_w	double precision;
vl_total_apres_mat_w 	double precision;
vl_total_apres_temp_w 	double precision :=0;

nr_seq_conta_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p
	and	ie_status <> 'C';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select coalesce(sum(vl_procedimento_imp),0)
	into STRICT   vl_total_apres_proc_w
	from   pls_conta_proc
	where  nr_seq_conta = nr_seq_conta_w
	and    ie_status <> 'D';

	select coalesce(sum(vl_material_imp),0)
	into STRICT   vl_total_apres_mat_w
	from   pls_conta_mat
	where  nr_seq_conta = nr_seq_conta_w
	and    ie_status <> 'D';

	vl_total_apres_temp_w := vl_total_apres_temp_w + vl_total_apres_proc_w + vl_total_apres_mat_w;

	end;
end loop;
close C01;

vl_total_apres_w := campo_mascara_virgula(vl_total_apres_temp_w);

return	vl_total_apres_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
