CREATE OR REPLACE FUNCTION obter_vl_atr_reg_tp_lote_cont ( nr_sequencia_p template_regra_lt_contabil.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

				
vl_atributo_w 			varchar(255);


BEGIN

    begin
		select (case 	cd_tipo_controle
        		when 1 then nm_tabela
           		when 2 then nm_dominio
           		when 3 then nm_funcao
               	end) ds_controle
		into STRICT 	vl_atributo_w
		from 	template_regra_lt_contabil
		where 	nr_sequencia = nr_sequencia_p;
    exception
		when no_data_found then vl_atributo_w := '';
	end;

	return vl_atributo_w;

end;
$body$
