CREATE OR REPLACE FUNCTION obter_vl_avaliacao_pj (cd_cgc_p text, dt_avaliacao_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;


BEGIN
select	sum(a.vl_media)
into STRICT	vl_retorno_w
from  	avf_resultado a
where	(a.vl_media IS NOT NULL AND a.vl_media::text <> '')
and	to_char(a.dt_avaliacao,'dd/mm/yyyy') = to_char(dt_avaliacao_p,'dd/mm/yyyy')
