CREATE OR REPLACE FUNCTION obter_vl_comprometido_tit_pag ( nr_seq_proj_rec_p bigint) RETURNS bigint AS $body$
DECLARE


vl_comprometido_w	projeto_recurso_saldo.vl_comprometido%type;


BEGIN

select  coalesce(sum(a.vl_titulo),0)
into STRICT	vl_comprometido_w
from    titulo_pagar a
where   a.nr_seq_proj_rec = nr_seq_proj_rec_p
and     ie_situacao = 'A'
and	coalesce(nr_seq_nota_fiscal::text, '') = '';

return	vl_comprometido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
