CREATE OR REPLACE FUNCTION obter_vl_cotacao_moeda ( cd_moeda_p bigint ) RETURNS bigint AS $body$
DECLARE


vl_cotacao_w   COTACAO_MOEDA.VL_COTACAO%type;


BEGIN
vl_cotacao_w := 0;

if (cd_moeda_p > 0) then
	begin
		select     coalesce(max(vl_cotacao),0) vl_cotacao
		into STRICT 	   vl_cotacao_w
		from       cotacao_moeda
		where      cd_moeda = cd_moeda_p
		and        coalesce(vl_cotacao,0) > 0
		order by   dt_cotacao desc;
	exception
	when others then
		vl_cotacao_w := 0;
	end;
end if;

return vl_cotacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
