CREATE OR REPLACE FUNCTION obter_vl_despesa_cartao (nr_seq_movto_p bigint) RETURNS bigint AS $body$
DECLARE


vl_despesa_w	double precision;


BEGIN

select	coalesce(sum(b.vl_despesa),0)
into STRICT	vl_despesa_w
FROM movto_cartao_cr a
LEFT OUTER JOIN movto_cartao_cr_parcela b ON (a.nr_sequencia = b.nr_seq_movto)
WHERE a.nr_sequencia	= nr_seq_movto_p;

return	vl_despesa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
