CREATE OR REPLACE FUNCTION obter_vl_fluxo_regra_fc (cd_empresa_p bigint, cd_conta_financ_p bigint, vl_classif_p bigint, cd_cgc_p text, nr_titulo_p bigint, cd_convenio_p bigint, dt_referencia_p timestamp, vl_juros_p bigint, vl_multa_p bigint, vl_descontos_p bigint, vl_outros_acresc_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w	double precision	:= 0;
vl_atual_tit_w	double precision;

cd_conta_fin_jcp_w	bigint;
cd_conta_fin_mcp_w	bigint;
cd_conta_fin_dcp_w	bigint;
cd_conta_fin_ocp_w	bigint;


BEGIN 
 
vl_atual_tit_w	:= (obter_dados_tit_pagar(nr_titulo_p, 'VT'))::numeric;
 
cd_conta_fin_jcp_w	:= obter_conta_financ_regra_fc(cd_empresa_p, 'JCP', cd_cgc_p,null,dt_referencia_p,cd_estabelecimento_p);
cd_conta_fin_mcp_w	:= obter_conta_financ_regra_fc(cd_empresa_p, 'MCP', cd_cgc_p,null,dt_referencia_p,cd_estabelecimento_p);
cd_conta_fin_dcp_w	:= obter_conta_financ_regra_fc(cd_empresa_p, 'DCP', cd_cgc_p,null,dt_referencia_p,cd_estabelecimento_p);
cd_conta_fin_ocp_w	:= obter_conta_financ_regra_fc(cd_empresa_p, 'OCP', cd_cgc_p,null,dt_referencia_p,cd_estabelecimento_p);
 
if (cd_conta_fin_jcp_w = cd_conta_financ_p) then 
	vl_retorno_w	:= vl_retorno_w + (dividir_sem_round(coalesce(vl_classif_p,vl_atual_tit_w),vl_atual_tit_w) * vl_juros_p);
end if;
 
if (cd_conta_fin_mcp_w = cd_conta_financ_p) then 
	vl_retorno_w	:= vl_retorno_w + (dividir_sem_round(coalesce(vl_classif_p,vl_atual_tit_w),vl_atual_tit_w) * vl_multa_p);
end if;
 
if (cd_conta_fin_dcp_w = cd_conta_financ_p) then 
	vl_retorno_w	:= vl_retorno_w + (dividir_sem_round(coalesce(vl_classif_p,vl_atual_tit_w),vl_atual_tit_w) * vl_descontos_p);
end if;
 
if (cd_conta_fin_ocp_w = cd_conta_financ_p) then 
	vl_retorno_w	:= vl_retorno_w + (dividir_sem_round(coalesce(vl_classif_p,vl_atual_tit_w),vl_atual_tit_w) * vl_outros_acresc_p);
end if;
 
 
return vl_retorno_w;
 
 
end;
$body$
