CREATE OR REPLACE FUNCTION obter_vl_fluxo_vencido (cd_conta_financ_p bigint, ie_restringe_estab_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_retorno_w		double precision	:= 0;


BEGIN

if (cd_conta_financ_p IS NOT NULL AND cd_conta_financ_p::text <> '') then
	select	coalesce(sum(b.vl_fluxo),0) vl_fluxo
	into STRICT	vl_retorno_w
	from   	fluxo_caixa b
	where	b.cd_conta_financ	= cd_conta_financ_p
	and (ie_restringe_estab_p = 'N' or (ie_restringe_estab_p = 'E' and b.cd_estabelecimento = cd_estabelecimento_p) or (ie_restringe_estab_p = 'S' and b.cd_estabelecimento = cd_estabelecimento_p))
	and     b.ie_classif_fluxo = 'V';
--	and 	exists
--			(select	1
--			from 	fluxo_caixa_v x
--			where 	x.cd_conta_financ = b.cd_conta_financ);
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
