CREATE OR REPLACE FUNCTION obter_vl_gravidade_api ( nr_prescricao_p bigint, nr_seq_material_p bigint, cd_api_p text, ie_detail_p text default null) RETURNS bigint AS $body$
DECLARE


	ds_retorno_w	bigint := 999;


BEGIN
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
		if (coalesce(ie_detail_p, 'S') = 'S') then
			/* Pegar a maior gravidade para o detalhe, deve olhar para a prescricao e seq_material */

			select 	case max_severity
						when 10 then 0 		/*MINOR*/
						when 90 then 1 		/*CONTRAINDICATED*/
						when 80 then 2 		/*MAJOR*/
						when 70 then 3 		/*MODERATE*/
						when 60 then 5 		/*INFO*/
						else 999			/*UNKNOWN*/
					end nr_order_severity
			  into STRICT 	ds_retorno_w
			  from (
					SELECT coalesce(max(nr_order_severity), 999) max_severity
					  from (
							SELECT 	case nr_gravidade
										when 0 then 10 		/*MINOR*/
										when 1 then 90 		/*CONTRAINDICATED*/
										when 2 then 80 		/*MAJOR*/
										when 3 then 70 		/*MODERATE*/
										when 4 then 70 		/*MODERATE*/
										when 5 then 60 		/*INFO*/
										else 0				/*UNKNOWN*/
									end nr_order_severity
							  from 	alerta_api
							 where 	nr_prescricao = nr_prescricao_p
							   and 	nr_seq_material = nr_seq_material_p
							   and 	cd_api = cd_api_p
							   and 	coalesce(ie_status_requisicao::text, '') = ''
							) alias6
					) alias7;
		else
			/* Pegar a maior gravidade para o grid, deve olhar somente para a prescricao */

			select 	case max_severity
						when 10 then 0 		/*MINOR*/
						when 90 then 1 		/*CONTRAINDICATED*/
						when 80 then 2 		/*MAJOR*/
						when 70 then 3 		/*MODERATE*/
						when 60 then 5 		/*INFO*/
						else 999			/*UNKNOWN*/
					end nr_order_severity
			  into STRICT 	ds_retorno_w
			  from (
					SELECT coalesce(max(nr_order_severity), 999) max_severity
					  from (
							SELECT 	case nr_gravidade
										when 0 then 10 		/*MINOR*/
										when 1 then 90 		/*CONTRAINDICATED*/
										when 2 then 80 		/*MAJOR*/
										when 3 then 70 		/*MODERATE*/
										when 4 then 70 		/*MODERATE*/
										when 5 then 60 		/*INFO*/
										else 0				/*UNKNOWN*/
									end nr_order_severity
							  from 	alerta_api
							 where 	nr_prescricao = nr_prescricao_p
							   and 	cd_api = cd_api_p
							   and 	coalesce(ie_status_requisicao::text, '') = ''
							) alias3
					) alias4;
		end if;
	end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
