SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_imposto_nf ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_imposto_w	NOTA_FISCAL_ITEM_TRIB.VL_TRIBUTO%type := 0;


BEGIN

if ( nr_interno_conta_p > 0) then
	select  coalesce(sum(vl_tributo),0) vl_tributo
	into STRICT    vl_total_imposto_w
	from    nota_fiscal_item_trib a,
		nota_fiscal_item      b,
		nota_fiscal           c
	where   a.nr_sequencia     = b.nr_sequencia
	and     a.nr_item_nf       = b.nr_item_nf
	and     b.nr_sequencia     = c.nr_sequencia
	and     c.nr_interno_conta = nr_interno_conta_p;
end if;

return	vl_total_imposto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
