CREATE OR REPLACE FUNCTION obter_vl_item_liquido_rateado ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


qt_itens_w			bigint;
vl_frete_w			double precision;
vl_item_liquido_w			double precision;
vl_despesa_acessoria_w		double precision;
vl_desconto_w			double precision;
pr_desconto_w			double precision;

BEGIN

select	count(*)
into STRICT	qt_itens_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p;

if (qt_itens_w > 0) then

	select	coalesce(vl_item_liquido,0)
	into STRICT	vl_item_liquido_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;

	select	CASE WHEN ie_frete='F' THEN coalesce(round((dividir(vl_frete,qt_itens_w))::numeric,2),0)  ELSE 0 END ,
		coalesce(round((dividir(vl_desconto,qt_itens_w))::numeric,2),0),
		coalesce(round((dividir(vl_despesa_acessoria,qt_itens_w))::numeric,2),0),
		coalesce(round(dividir((dividir((coalesce(pr_desconto,0) * 100),vl_item_liquido_w)),qt_itens_w),2),0)
	into STRICT	vl_frete_w,
		vl_desconto_w,
		vl_despesa_acessoria_w,
		pr_desconto_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_p;

	vl_item_liquido_w := vl_item_liquido_w + vl_frete_w - vl_desconto_w - pr_desconto_w + vl_despesa_acessoria_w;
end if;

return	coalesce(vl_item_liquido_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
