CREATE OR REPLACE FUNCTION obter_vl_itens_ordem_nota (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


			
nr_ordem_compra_w			bigint;
vl_item_liquido_w			double precision;
vl_item_liquido_ww			double precision := 0;
vl_frete_w				double precision := 0;
vl_frete_ww				double precision := 0;
vl_ipi_w				double precision := 0;
vl_iva_w				double precision := 0;
vl_icms_w				double precision := 0;
vl_despesa_acessoria_w			double precision;
vl_seguro_w				ordem_compra.vl_seguro%type;
vl_icmsst_w             ordem_compra_item_trib.vl_tributo%type;
			
c01 CURSOR FOR
SELECT	distinct nr_ordem_compra
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p;
			

BEGIN

open C01;
loop
fetch C01 into	
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	coalesce(sum(a.vl_tributo),0)
	into STRICT	vl_ipi_w
	from	ordem_compra_item_trib a,
		tributo b
	where	a.cd_tributo = b.cd_tributo
	and	a.nr_ordem_compra = nr_ordem_compra_w
	and	b.ie_tipo_tributo = 'IPI';
	
	select	coalesce(sum(a.vl_tributo),0)
	into STRICT	vl_iva_w
	from	ordem_compra_item_trib a,
		tributo b
	where	a.cd_tributo = b.cd_tributo
	and	a.nr_ordem_compra = nr_ordem_compra_w
	and	b.ie_tipo_tributo = 'IVA';

    select	coalesce(sum(a.vl_tributo),0)
	into STRICT	vl_icmsst_w
	from	ordem_compra_item_trib a,
		tributo b
	where	a.cd_tributo = b.cd_tributo
	and	a.nr_ordem_compra = nr_ordem_compra_w
	and	b.ie_tipo_tributo = 'ICMSST';

	select	coalesce(CASE WHEN ie_frete='F' THEN vl_frete  ELSE 0 END ,0),
			coalesce(vl_seguro,0)
	into STRICT	vl_frete_w,
			vl_seguro_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_w;
	
	select	coalesce(vl_despesa_acessoria,0)
	into STRICT	vl_despesa_acessoria_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_w;
	
	select	sum(vl_item_liquido)
	into STRICT	vl_item_liquido_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_w;
	
	vl_frete_ww := vl_frete_ww + vl_frete_w;
	vl_item_liquido_ww := vl_item_liquido_ww + vl_item_liquido_w + vl_ipi_w + vl_iva_w + vl_despesa_acessoria_w + vl_seguro_w + vl_icmsst_w;
	
	end;
end loop;
close C01;


return	vl_item_liquido_ww + vl_frete_ww;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_vl_itens_ordem_nota (nr_sequencia_p bigint) FROM PUBLIC;

