CREATE OR REPLACE FUNCTION obter_vl_lookup_agenda (cd_agenda_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);


BEGIN 
 
select	SUBSTR(CASE WHEN a.cd_tipo_agenda=4 THEN  a.ds_curta WHEN a.cd_tipo_agenda=3 THEN  CASE WHEN obter_nome_medico_combo_agcons(a.cd_estabelecimento, a.cd_agenda, a.cd_tipo_agenda, CASE WHEN coalesce(a.ie_ordenacao::text, '') = '' THEN  'N'  ELSE a.ie_ordenacao END )=wheb_mensagem_pck.get_texto(802928) THEN  wheb_mensagem_pck.get_texto(802929)||' '||a.cd_agenda||' ('||wheb_mensagem_pck.get_texto(802931)||') - '||coalesce(obter_desc_espec_agenda(a.cd_especialidade), coalesce(a.ds_curta, wheb_mensagem_pck.get_texto(802932)))  ELSE obter_nome_medico_combo_agcons(a.cd_estabelecimento, a.cd_agenda, a.cd_tipo_agenda, CASE WHEN coalesce(a.ie_ordenacao::text, '') = '' THEN  'N'  ELSE a.ie_ordenacao END ) END   ELSE ds_agenda END , 1, 255) ds 
into STRICT	ds_retorno_w 
from	agenda a 
where	a.ie_situacao = 'A' 
and	a.cd_tipo_agenda in 3 
and	a.cd_estabelecimento = cd_estabelecimento_p 
and	a.cd_agenda = cd_agenda_p;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
