CREATE OR REPLACE FUNCTION obter_vl_materiais_nf ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

vl_retorno_w	double precision;

BEGIN
select	round((coalesce(sum(a.vl_liquido),0))::numeric,2)
into STRICT	vl_retorno_w
from 	nota_fiscal_item a,
	material b
where	a.cd_material = b.cd_material
and	coalesce(a.cd_procedimento::text, '') = ''
and	coalesce(fis_obter_tipo_mat_fiscal(b.ie_tipo_material),'M') = 'M'
and	a.nr_sequencia = nr_sequencia_p;

return	vl_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
