SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_mat_autor ( nr_seq_matpaci bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w		bigint;
vl_retorno_w			bigint;
cd_material_w			integer;
nr_seq_opme_w			bigint;

pr_adicional_w			double precision;
ie_reducao_acrescimo_w		varchar(1);
cd_convenio_w			bigint;
cd_estabelecimento_w		integer;


BEGIN

vl_retorno_w		:= 0;


if (coalesce(nr_seq_matpaci,0) > 0 ) then


	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

	select	max(a.nr_atendimento),
		max(a.cd_material),
		max(a.nr_seq_agenda_pac_opme),
		max(a.cd_convenio)
	into STRICT	nr_atendimento_w,
		cd_material_w,
		nr_seq_opme_w,
		cd_convenio_w
	from	material_atend_paciente a
	where	a.nr_sequencia 	= nr_seq_matpaci;


	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

			select	coalesce(SUM(vl_unitario),0)
			into STRICT	vl_retorno_w
			from	material_autorizado a,
				autorizacao_convenio b
			where	a.nr_sequencia_autor	= b.nr_sequencia
			and	a.cd_material		= cd_material_w
			and	b.nr_atendimento	= nr_atendimento_w
			and	((a.nr_seq_opme		= nr_seq_opme_w) or (coalesce(a.nr_seq_opme::text, '') = ''));


			if (coalesce(vl_retorno_w,0) = 0) then
				begin

				begin
				SELECT * FROM obter_tx_reducao_acresc_conv(cd_convenio_w, cd_estabelecimento_w, clock_timestamp(), cd_material_w, null, null, null, pr_adicional_w, ie_reducao_acrescimo_w) INTO STRICT pr_adicional_w, ie_reducao_acrescimo_w;
				exception
				when others then
					pr_adicional_w := 0;
					ie_reducao_acrescimo_w := 'A';
				end;


				select	coalesce(max(CASE WHEN ie_reducao_acrescimo_w='R' THEN 					(coalesce(vl_unitario_item,0) - (coalesce(vl_unitario_item,0)  * coalesce(pr_adicional_w/100,0)))  ELSE (coalesce(vl_unitario_item,0)  + (coalesce(vl_unitario_item,0)  * coalesce(pr_adicional_w/100,0))) END ),0)
				into STRICT	vl_retorno_w
				from	agenda_pac_opme
				where	nr_sequencia = nr_seq_opme_w;
				end;
			end if;

	end if;

end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
