CREATE OR REPLACE FUNCTION obter_vl_mat_repasse_adic ( nr_seq_criterio_p bigint, nr_seq_matpaci_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			varchar(1);
nr_seq_adic_w		bigint;
vl_retorno_w			double precision;
ds_function_w		varchar(255);
ds_comando_aux_w	varchar(2000);
ds_comando_w		varchar(2000);


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT		nr_seq_adic_w
from 	mat_criterio_repasse
where	nr_sequencia	= nr_seq_criterio_p;

if (nr_seq_adic_w > 0) then

	select 	upper(ds_function)
	into STRICT		ds_function_w
	from 	mat_criterio_repasse
	where	nr_sequencia	= nr_seq_adic_w;

	ds_comando_aux_w	:= replace(ds_function_w, ':NR_SEQ_MATPACI', nr_seq_matpaci_p);

	ds_comando_w		:= 'select ' ||  ds_comando_aux_w || ' from dual';

	vl_retorno_w := obter_Valor_Dinamico(ds_comando_w, vl_retorno_w);

end if;

return	vl_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
