SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_medico_fora_pacote (nr_seq_propaci_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_original_w			double precision := 0;
vl_participante_w		double precision := 0;
vl_medico_w			double precision := 0;


BEGIN 
 
select	coalesce(sum(vl_procedimento),0) 
into STRICT	vl_original_w 
from 	proc_paciente_valor 
where	nr_seq_procedimento	= nr_seq_propaci_p 
and	ie_tipo_valor		= 1;
 
select	coalesce(sum(vl_participante),0) 
into STRICT	vl_participante_w 
from	procedimento_participante 
where	nr_sequencia		= nr_seq_propaci_p;
 
vl_medico_w	:= vl_original_w - vl_participante_w;
 
return	vl_medico_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_vl_medico_fora_pacote (nr_seq_propaci_p bigint) FROM PUBLIC;

