CREATE OR REPLACE FUNCTION obter_vl_mercadoria_nf_titulo (nr_titulo_p titulo_pagar.nr_titulo%Type) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	nota_fiscal.vl_mercadoria%Type;


BEGIN

begin

select	a.vl_mercadoria
into STRICT	vl_retorno_w
from	nota_fiscal a,
	titulo_pagar b
where	b.nr_titulo = nr_titulo_p
and	a.nr_sequencia = b.nr_seq_nota_fiscal;

exception
	when others then
	vl_retorno_w := null;
end;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
