CREATE OR REPLACE FUNCTION obter_vl_orcado_empenhado_ano ( cd_conta_contabil_p text, cd_centro_custo_p bigint, cd_Estabelecimento_p bigint, dt_ano_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_Sequencia_w		bigint;
dt_mes_w		timestamp;		
vl_empenhado_w		double precision := 0;
vl_empenhado_ww		double precision := 0;
vl_orcado_w		double precision := 0;
vl_orcado_ww		double precision := 0;
vl_resultado_w		double precision := 0;
			
c01 CURSOR FOR
SELECT	nr_Sequencia
from	ctb_mes_ref
where	dt_referencia between ESTABLISHMENT_TIMEZONE_UTILS.startOfYear(dt_ano_referencia_p) and ESTABLISHMENT_TIMEZONE_UTILS.endOfYear(dt_ano_referencia_p)
order by 1;

c02 CURSOR FOR
SELECT	dt_mes
from	mes_v
where	dt_mes between ESTABLISHMENT_TIMEZONE_UTILS.startOfYear(dt_ano_referencia_p) and ESTABLISHMENT_TIMEZONE_UTILS.endOfYear(dt_ano_referencia_p)
order by	1;
			

BEGIN

if (ie_opcao_p = 'E') then

	open C01;
	loop
	fetch C01 into	
		nr_Sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		select	coalesce(ctb_obter_empenho_orcamento(nr_Sequencia_w, cd_conta_contabil_p, cd_centro_custo_p, cd_Estabelecimento_p),0)  vl_empenhado
		into STRICT	vl_empenhado_w
		;
		
		vl_empenhado_ww := vl_empenhado_ww + vl_empenhado_w;
		
		end;
	end loop;
	close C01;
	
	vl_resultado_w := vl_empenhado_ww;
end if;

if (ie_opcao_p = 'O') then
	
	open C02;
	loop
	fetch C02 into	
		dt_mes_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	coalesce(ctb_obter_valor_orcado(dt_mes_w, cd_conta_contabil_p, cd_centro_custo_p, cd_Estabelecimento_p),0) vl_orcado
		into STRICT	vl_orcado_w
		;
		
		vl_orcado_ww := vl_orcado_ww + vl_orcado_w;
		
		end;
	end loop;
	close C02;
	
	vl_resultado_w := vl_orcado_ww;
end if;
			
return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
