CREATE OR REPLACE FUNCTION obter_vl_pago_dig_item (nr_seq_propaci_p bigint, nr_seq_partic_p bigint, nr_seq_matpaci_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;


BEGIN



if (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') and (nr_seq_partic_p IS NOT NULL AND nr_seq_partic_p::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	vl_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_propaci	= nr_seq_propaci_p
	and	nr_seq_partic	= nr_seq_partic_p;

elsif (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	vl_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_propaci	= nr_seq_propaci_p
	and	coalesce(nr_seq_partic::text, '') = '';

elsif (nr_seq_matpaci_p IS NOT NULL AND nr_seq_matpaci_p::text <> '') then

	select	sum(vl_pago_digitado)
	into STRICT	vl_retorno_w
	from	convenio_retorno_glosa
	where	nr_seq_matpaci	= nr_seq_matpaci_p;

end if;

return coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
