CREATE OR REPLACE FUNCTION obter_vl_prox_rendimento (nr_seq_aplicacao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_prox_rend_w		double precision;


BEGIN

if (nr_seq_aplicacao_p IS NOT NULL AND nr_seq_aplicacao_p::text <> '') then
	begin
	select 	CASE WHEN coalesce(a.vl_aplicacao_estrang,0)=0 THEN		(((a.vl_aplicacao + coalesce(sum(b.vl_rendimento),0)+  coalesce(sum(d.vl_deposito),0)) * c.vl_taxa_rend)/100)  ELSE (((a.vl_aplicacao_estrang +            coalesce(sum(b.vl_rendimento_estrang),0) +            coalesce(sum(d.vl_deposito_estrang),0)) * c.vl_taxa_rend)/100) END  vl_rendimento
	into STRICT	vl_prox_rend_w
	FROM conta_banco_tipo_aplic c, banco_aplicacao a
LEFT OUTER JOIN banco_rendimento b ON (a.nr_sequencia = b.nr_seq_aplicacao)
LEFT OUTER JOIN banco_aplic_deposito d ON (a.nr_sequencia = d.nr_seq_aplicacao)
WHERE c.nr_sequencia = a.nr_seq_aplicacao  and a.nr_sequencia = nr_seq_aplicacao_p group by a.vl_aplicacao_estrang,a.vl_aplicacao,c.vl_taxa_rend;
	exception when others then
	vl_prox_rend_w := null;
	end;
end if;

return vl_prox_rend_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
