CREATE OR REPLACE FUNCTION obter_vl_rateio_estab ( dt_referencia_p timestamp, cd_estabelecimento_p bigint, nr_seq_bem_p bigint) RETURNS bigint AS $body$
DECLARE

vl_retorno_w		pat_valor_bem_rateio.vl_rateio_deprec%Type;
vl_rateio_deprec_w	pat_valor_bem_rateio.vl_rateio_deprec%Type;

BEGIN

select 	sum(a.vl_rateio_deprec)
into STRICT	vl_rateio_deprec_w
from 	pat_valor_bem_rateio a,
	pat_valor_bem b,
	centro_custo c
where	a.cd_centro_custo 	= c.cd_centro_custo
and	c.cd_estabelecimento 	= cd_estabelecimento_p
and	a.nr_seq_valor		= b.nr_sequencia
and	b.nr_seq_bem 		= nr_seq_bem_p
and	b.dt_valor between trunc(dt_referencia_p,'month') AND LAST_DAY(TRUNC(dt_referencia_p,'month')) + 86399/86400;

vl_retorno_w := vl_rateio_deprec_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
