CREATE OR REPLACE FUNCTION obter_vl_receb_titulo (nr_titulo_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_recebido_w	double precision;


BEGIN

select	sum(b.vl_recebido)
into STRICT	vl_recebido_w
from	convenio_receb a,
	convenio_receb_titulo b
where	a.nr_sequencia		= b.nr_seq_receb
and	b.nr_titulo		= nr_titulo_p
and	a.dt_Recebimento	<= dt_referencia_p;

return	 vl_recebido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
