CREATE OR REPLACE FUNCTION obter_vl_recuperar_ret_item (nr_seq_ret_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w    double precision;


BEGIN

select  sum(vl_arecuperar)
into STRICT    vl_retorno_w
from    convenio_retorno_glosa
where   nr_seq_ret_item = nr_seq_ret_item_p;

return  coalesce(vl_retorno_w,0);

end;
