SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_recurso_titulo ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_liq_w		titulo_receber_liq.nr_sequencia%type;
nr_seq_retorno_w	titulo_receber_liq.nr_seq_retorno%type;
nr_seq_lote_hist_guia_w	titulo_receber_liq.nr_seq_lote_hist_guia%type;
vl_recurso_w		titulo_receber_liq.vl_recurso%type;
vl_recebido_w		titulo_receber_liq.vl_recebido%type;
vl_glosa_w		titulo_receber_liq.vl_glosa%type;
ie_recurso_glosa_tit_rec_w	parametro_fluxo_caixa.ie_recurso_glosa_tit_rec%type;
cd_estabelecimento_w		titulo_receber.cd_estabelecimento%type;


BEGIN

/* obter a Ãºltima baixa que teve valor recursado */

select	max(a.nr_sequencia)
into STRICT	nr_seq_liq_w
from	titulo_receber_liq a
where	not exists (SELECT	1
	from	titulo_receber_liq x
	where	x.nr_seq_liq_origem	= a.nr_sequencia
	and	x.nr_titulo		= a.nr_titulo)
and	coalesce(a.nr_seq_liq_origem::text, '') = ''
and	coalesce(a.vl_recurso,0)	<> 0
and	a.nr_titulo		= nr_titulo_p;

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	titulo_receber a
where	a.nr_titulo = nr_titulo_p;

if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then

	select	coalesce(max(a.ie_recurso_glosa_tit_rec), 'S')
	into STRICT	ie_recurso_glosa_tit_rec_w
	from	parametro_fluxo_caixa a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;

end if;

if (coalesce(nr_seq_liq_w::text, '') = '') then

	vl_recurso_w	:= 0;

else

	select	coalesce(max(a.vl_recurso),0),
		max(a.nr_seq_retorno),
		max(a.nr_seq_lote_hist_guia)
	into STRICT	vl_recurso_w,
		nr_seq_retorno_w,
		nr_seq_lote_hist_guia_w
	from	titulo_receber_liq a
	where	a.nr_sequencia		= nr_seq_liq_w
	and	a.nr_titulo		= nr_titulo_p;

	select	coalesce(sum(a.vl_recebido),0),
		coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_recebido_w,
		vl_glosa_w
	from	titulo_receber_liq a
	where	not exists (SELECT	1
		from	titulo_receber_liq x
		where	x.nr_seq_liq_origem	= a.nr_sequencia
		and	x.nr_titulo		= a.nr_titulo)
	and	coalesce(a.nr_seq_liq_origem::text, '') = ''
	and	(((a.nr_seq_retorno IS NOT NULL AND a.nr_seq_retorno::text <> '') and a.nr_seq_retorno <> nr_seq_retorno_w) or ((a.nr_seq_lote_hist_guia IS NOT NULL AND a.nr_seq_lote_hist_guia::text <> '') and a.nr_seq_lote_hist_guia <> nr_seq_lote_hist_guia_w))
	and	a.nr_sequencia		> nr_seq_liq_w
	and	a.nr_titulo		= nr_titulo_p;

	vl_recurso_w	:= vl_recurso_w - vl_recebido_w - vl_glosa_w;

	if (vl_recurso_w	< 0) then

		vl_recurso_w	:= 0;

	end if;

end if;

if (coalesce(ie_recurso_glosa_tit_rec_w,'S') = 'N') then
	vl_recurso_w	:= 0;
end if;

return vl_recurso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_vl_recurso_titulo ( nr_titulo_p bigint) FROM PUBLIC;

