CREATE OR REPLACE FUNCTION obter_vl_rec_consultoria ( dt_mes_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint;
vl_rec_consultoria_w	double precision;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from 	ctb_movimento
where 	cd_conta_credito = '2070'
and 	trunc(dt_movimento,'month') = trunc(dt_mes_referencia_p,'month');

if (qt_registros_w > 0) then

	select	coalesce(vl_movimento,0)
	into STRICT	vl_rec_consultoria_w
	from 	ctb_movimento
	where 	cd_conta_credito = '2070'
	and 	trunc(dt_movimento,'month') = trunc(dt_mes_referencia_p,'month');

else
	vl_rec_consultoria_w := 0;
end if;

return	replace(campo_mascara(vl_rec_consultoria_w,2),'.',',');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
