CREATE OR REPLACE FUNCTION obter_vl_reg_cobr_item_acresc ( nr_seq_registro_p bigint, ie_atual_prevista_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision := 0;
dt_prev_pagto_w		timestamp;
nr_seq_cheque_w		bigint;
nr_titulo_w		bigint;
dt_referencia_w		timestamp;


BEGIN
if (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '') then
	select	clock_timestamp(),
		b.nr_seq_cheque,
		b.nr_titulo,
		coalesce(c.dt_prev_pagto,clock_timestamp())
	into STRICT	dt_prev_pagto_w,
		nr_seq_cheque_w,
		nr_titulo_w,
		dt_prev_pagto_w
	from	registro_cobranca c,
		cobranca b,
		registro_cobr_item a
	where 	a.nr_seq_cobranca	= b.nr_sequencia
	and	a.nr_seq_registro	= c.nr_sequencia
	and	a.nr_sequencia		= nr_seq_registro_p;

	if (ie_atual_prevista_p = 'A') then
		dt_referencia_w	:= clock_timestamp();
	elsif (ie_atual_prevista_p = 'P') then
		dt_referencia_w	:= dt_prev_pagto_w;
	end if;

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
		vl_retorno_w := coalesce(obter_juros_multa_titulo(nr_titulo_w, dt_referencia_w, 'R', 'A'),0);
	elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then
		vl_retorno_w := coalesce(obter_juro_multa_cheque(nr_seq_cheque_w, dt_referencia_w, 'R', 'A'),0);
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
