SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_saldo_adiant_conta (nr_interno_conta_p bigint, ie_vinculado_p text) RETURNS bigint AS $body$
DECLARE

 
vl_adiantamento_w	double precision	:= 0;


BEGIN 
 
if (ie_vinculado_p	= 'N') then 
	select	sum(VL_saldo) 
	into STRICT	vl_adiantamento_w 
	from	CONTA_PAC_ADIANT_V 
	where	nr_interno_conta	= nr_interno_conta_p;
else 
	select	sum(b.VL_saldo) 
	into STRICT	vl_adiantamento_w 
	from	adiantamento b, conta_paciente_adiant a 
	where	a.nr_interno_conta	= nr_interno_conta_p 
	and	a.nr_adiantamento	= b.nr_adiantamento;
end if;
 
return	vl_adiantamento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
