SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_vl_servicos_nf ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


vl_servicos_w		double precision;
vl_procedimentos_w	double precision;
vl_retorno_w		double precision;

BEGIN
select	coalesce(sum(a.vl_liquido),0)
into STRICT	vl_procedimentos_w
from 	nota_fiscal_item a
where	(a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '')
and	a.nr_sequencia = nr_sequencia_p;

select	coalesce(sum(a.vl_liquido),0)
into STRICT	vl_servicos_w
from 	nota_fiscal_item a,
	material b
where	a.cd_material = b.cd_material
and	coalesce(a.cd_procedimento::text, '') = ''
and	coalesce(fis_obter_tipo_mat_fiscal(b.ie_tipo_material),'M') = 'S'
and	a.nr_sequencia = nr_sequencia_p;

vl_retorno_w := round((vl_procedimentos_w + vl_servicos_w)::numeric,2);

return vl_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_vl_servicos_nf ( nr_sequencia_p bigint) FROM PUBLIC;

