CREATE OR REPLACE FUNCTION obter_vl_titulo_bordero_pagar (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_bordero_w		bordero_pagamento.nr_bordero%type;
vl_bordero_w		bordero_tit_pagar.vl_bordero%type;
vl_retorno_w		bordero_tit_pagar.vl_bordero%type;

/*Function criada para identificar  o valor que o titulo foi recebido com cheque no bordero para consistir a geração do cheque.  Essa function é utilizada no CorCpa_F5, gerar_titulo_cheque_wdlgOkClick
Muito cuidado ao alterá-la
Somente consiste títulos que estiverem em borderôs que foram criados com o parâmetro 12 do borderô como Sim, pois ai usa a bordero_tit_pagar e não o titulo_pagar.*/


/*Cursor para buscar todos os borderôs que o título está*/

c01 CURSOR FOR
	SELECT	nr_bordero
	from	titulo_pagar_bordero_v
	where	nr_titulo = nr_titulo_p;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		nr_bordero_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '') then

			select	coalesce(sum(c.vl_cheque),0)
			into STRICT	vl_bordero_w
			from	bordero_tit_pagar a,
					cheque_bordero_titulo b,
					cheque c
			where	a.nr_bordero	= b.nr_bordero
			and		b.nr_seq_cheque = c.nr_sequencia
			and		coalesce(c.dt_cancelamento::text, '') = ''
			and		a.nr_bordero 	= nr_bordero_w
			and		a.nr_titulo		= nr_titulo_p
			and		b.nr_titulo		= nr_titulo_p;

		end if;

		vl_retorno_w := coalesce(vl_retorno_w,0) + coalesce(vl_bordero_w,0);

		end;
	end loop;
	close c01;

end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_vl_titulo_bordero_pagar (nr_titulo_p bigint) FROM PUBLIC;

