CREATE OR REPLACE FUNCTION obter_vl_total_req_mat ( nr_requisicao_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*
Criado o ie_opcao caso seja necessário implementações futuras.
1 - Total do vl unitario previsto dos itens da requisição

*/
vl_retorno_w	double precision;


BEGIN
if (nr_requisicao_p > 0) then
	begin
	if (ie_opcao_p = '1') then
		select	coalesce(sum(coalesce(vl_unit_previsto,0)),0)
		into STRICT	vl_retorno_w
		from	item_requisicao_material
		where	nr_requisicao = nr_requisicao_p;
	end if;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
