CREATE OR REPLACE FUNCTION obter_vl_total_trib_nota ( nr_sequencia_p bigint, ie_tipo_tributo_p text) RETURNS bigint AS $body$
DECLARE


ie_tipo_tributo_w			varchar(15);
vl_trib_item_w			double precision;
vl_trib_nota_w			double precision;
vl_retorno_w			double precision;


BEGIN

ie_tipo_tributo_w		:= coalesce(ie_tipo_tributo_p,'X');

select	coalesce(sum(n.vl_tributo),0)
into STRICT	vl_trib_item_w
from	tributo t,
	nota_fiscal_item_trib n
where	t.cd_tributo = n.cd_tributo
and	n.nr_sequencia = nr_sequencia_p
and	((ie_tipo_tributo_w = 'X') or (t.ie_tipo_tributo = ie_tipo_tributo_w));

select	coalesce(sum(n.vl_tributo),0)
into STRICT	vl_trib_nota_w
from	tributo t,
	nota_fiscal_trib n
where	t.cd_tributo = n.cd_tributo
and	n.nr_sequencia = nr_sequencia_p
and	((ie_tipo_tributo_w = 'X') or (t.ie_tipo_tributo = ie_tipo_tributo_w));

vl_retorno_w	:= (vl_trib_item_w + vl_trib_nota_w);

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
