CREATE OR REPLACE FUNCTION obter_vl_tributo_item_nf (nr_seq_nota_p bigint, cd_codigo_variacao_fis_p text, ie_tipo_tributo_p text) RETURNS bigint AS $body$
DECLARE

vl_retorno_w 				double precision;
cd_material_w				bigint;
nr_sequencia_nf_w				bigint;
nr_item_nf_w				bigint;
nr_seq_variacao_fis_w			bigint;
cd_codigo_var_fis_w			varchar(2);
vl_credito_imposto_w			double precision;
vl_isento_nao_tributada_w			double precision;
vl_outros_w				double precision;
vl_total_credito_imp_w			double precision := 0;
vl_total_isento_nao_trib_w			double precision := 0;
vl_total_outros_w				double precision := 0;
			
			 
c01 CURSOR FOR 
 
SELECT 	nr_sequencia, 
	nr_item_nf, 
	cd_material 
from 	nota_fiscal_item 
where 	nr_sequencia = nr_seq_nota_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_nf_w, 
	nr_item_nf_w, 
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	select	obter_dados_variacao_fiscal(nr_sequencia_nf_w,cd_material_w,'S') 
	into STRICT	nr_seq_variacao_fis_w 
	;
	 
	if (nr_seq_variacao_fis_w > 0) then 
		select 	obter_cd_valor_fiscal(nr_seq_variacao_fis_w) 
		into STRICT	cd_codigo_var_fis_w 
		;	
	 
		if (cd_codigo_var_fis_w = '1') then 
			select 	sum(coalesce(vl_tributo,0)) 
			into STRICT	vl_credito_imposto_w 
			from	nota_fiscal_item_trib a, 
				tributo b, 
				nota_fiscal_item c 
			where	a.cd_tributo 	= 	b.cd_tributo 
			and	a.nr_sequencia 	= 	c.nr_sequencia 
			and	a.nr_item_nf	=	c.nr_item_nf 
			and	a.nr_sequencia	=	nr_sequencia_nf_w 
			and	b.ie_tipo_tributo	=	ie_tipo_tributo_p	 
			and	a.nr_item_nf	=	nr_item_nf_w;
			 
			vl_total_credito_imp_w := (vl_total_credito_imp_w + vl_credito_imposto_w);
			 
		elsif (cd_codigo_var_fis_w = '2') then 
			select 	sum(coalesce(vl_tributo,0)) 
			into STRICT	vl_isento_nao_tributada_w 
			from	nota_fiscal_item_trib a, 
				tributo b, 
				nota_fiscal_item c 
			where	a.cd_tributo 	= 	b.cd_tributo 
			and	a.nr_sequencia 	= 	c.nr_sequencia 
			and	a.nr_item_nf	=	c.nr_item_nf 
			and	a.nr_sequencia	=	nr_sequencia_nf_w 
			and	b.ie_tipo_tributo	=	ie_tipo_tributo_p	 
			and	a.nr_item_nf	=	nr_item_nf_w;
			 
			vl_total_isento_nao_trib_w	:= (vl_total_isento_nao_trib_w + vl_isento_nao_tributada_w);
			 
		elsif (cd_codigo_var_fis_w = '3') then 
			select 	sum(coalesce(vl_tributo,0)) 
			into STRICT	vl_outros_w 
			from	nota_fiscal_item_trib a, 
				tributo b, 
				nota_fiscal_item c 
			where	a.cd_tributo 	= 	b.cd_tributo 
			and	a.nr_sequencia 	= 	c.nr_sequencia 
			and	a.nr_item_nf	=	c.nr_item_nf 
			and	a.nr_sequencia	=	nr_sequencia_nf_w 
			and	b.ie_tipo_tributo	=	ie_tipo_tributo_p	 
			and	a.nr_item_nf	=	nr_item_nf_w;
			 
			vl_total_outros_w := (vl_total_outros_w + vl_outros_w);
			 
		end if;
	 
	end if;
	end;
end loop;
close c01;
 
if (cd_codigo_variacao_fis_p = 1) then 
	vl_retorno_w := vl_total_credito_imp_w;
elsif (cd_codigo_variacao_fis_p = 2) then 
	vl_retorno_w := vl_total_isento_nao_trib_w;
elsif (cd_codigo_variacao_fis_p = 3) then 
	vl_retorno_w := vl_total_outros_w;
end if;
 
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
