CREATE OR REPLACE FUNCTION obter_vl_tributo_item ( nr_sequencia_p bigint, nr_item_nf_p bigint, cd_tributo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
VB: valor da base de cálculo do tributo
VT: valor do tributo
*/
retorno_w		double precision;
qt_registros_w		bigint;
vl_item_w		double precision;


BEGIN

select 	vl_total_item_nf
into STRICT	vl_item_w
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	nr_item_nf = nr_item_nf_p;

if (ie_opcao_p = 'VB') then
	retorno_w := vl_item_w;
end if;

-- Verificar se a nota tem o tributo passado como parâmetro
select  count(*)
into STRICT	qt_registros_w
from	nota_fiscal_trib t
where	t.nr_sequencia = nr_sequencia_p
and		cd_tributo = cd_tributo_p
and		vl_tributo > 0;

if (qt_registros_w > 0) then
	if (ie_opcao_p = 'VT') then
		retorno_w := ((vl_item_w * obter_pr_imposto(cd_tributo_p))/100);
	end if;
else
	retorno_w := 0;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
