CREATE OR REPLACE FUNCTION obter_vl_tributo ( nr_seq_nota_p bigint, cd_tributo_p bigint, ie_opcao_p text default 'VT', ie_utilizacao_p text default 'DACON') RETURNS bigint AS $body$
DECLARE

/* Rotina feita principalmente para a DACON
VT - Valor do tributo
VB - Valor da base de cálculo do PIS ou do COFINS

Caso a rotina esteja sendo utilizada para a DACON deve-se informar 'DACON' no parâmetro 'IE_UTILIZACAO_P
Para calcular o valor de base de cálculo ou o valor do tributo deve-se informar 'GERAL'.

*/
vl_resultado_w  double precision;
qt_registro_w   bigint;


BEGIN

if (ie_utilizacao_p = 'DACON') then

        if (ie_opcao_p = 'VT') then
                select  coalesce(max(coalesce(vl_tributo,0)),0)
                into STRICT    vl_resultado_w
                from    nota_fiscal_trib c
                where   c.nr_sequencia = nr_seq_nota_p
                and     c.cd_tributo = cd_tributo_p;
        elsif (ie_opcao_p = 'VB') then
                select  count(*)
                into STRICT    qt_registro_w
                from    nota_fiscal_trib c
                where   c.nr_sequencia = nr_seq_nota_p
                and (c.cd_tributo = cd_tributo_p);
                if (qt_registro_w > 0) then
                        select  coalesce(max(c.vl_base_calculo),0)
                        into STRICT    vl_resultado_w
                        from    nota_fiscal_trib c
                        where   c.nr_sequencia = nr_seq_nota_p
                        and     c.cd_tributo = cd_tributo_p;
                elsif (qt_registro_w = 0) then
                        vl_resultado_w := 0;
                end if;
        end if;
elsif (ie_utilizacao_p = 'GERAL') then
        if (ie_opcao_p = 'VT') then
                select  coalesce(max(coalesce(vl_tributo,0)),0)
                into STRICT    vl_resultado_w
                from    nota_fiscal_trib c
                where   c.nr_sequencia = nr_seq_nota_p
                and     c.cd_tributo = cd_tributo_p;
        elsif (ie_opcao_p = 'VB') then
                select  count(*)
                into STRICT    qt_registro_w
                from    nota_fiscal_trib c
                where   c.nr_sequencia = nr_seq_nota_p
                and (c.cd_tributo = cd_tributo_p);
                if (qt_registro_w > 0) then
                        select  coalesce(max(c.vl_base_calculo),0)
                        into STRICT    vl_resultado_w
                        from    nota_fiscal_trib c
                        where   c.nr_sequencia = nr_seq_nota_p
                        and     c.cd_tributo = cd_tributo_p;
                elsif (qt_registro_w = 0) then
                        vl_resultado_w := 0;
                end if;
        end if;
end if;

return vl_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
