CREATE OR REPLACE FUNCTION obter_vl_ult_resgate (nr_seq_aplicacao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_ult_resg_w		double precision;


BEGIN

if (nr_seq_aplicacao_p IS NOT NULL AND nr_seq_aplicacao_p::text <> '') then
	begin
	select	coalesce(coalesce(vl_resgate_estrang,vl_resgate),0) vl_resgate
	into STRICT	vl_ult_resg_w
	from 	banco_resgate
	where 	nr_seq_aplicacao = nr_seq_aplicacao_p
	and 	dt_resgate = 	(SELECT max(x.dt_resgate)
				from banco_resgate x
				where x.nr_seq_aplicacao = nr_seq_aplicacao_p);
	exception when others then
	vl_ult_resg_w := null;
	end;
end if;

return vl_ult_resg_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
